public class JavaScriptInterface {
    private Context context;
    String TYPE_XL = "application/vnd.ms-excel";
    String TYPE_PDF = "application/pdf";
    String TYPE_SPREAD_SHEET = "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet";
    Activity activity;
    public JavaScriptInterface(Context context, Activity activity) {
        this.context = context;
        this.activity = activity;
    }

    @JavascriptInterface
    public void getBase64FromBlobData(String base64Data) throws IOException {
        convertBase64StringToPdfAndStoreIt(base64Data);
    }
    public static String getBase64StringFromBlobUrl(String blobUrl) {
        if(blobUrl.startsWith("blob")){
            return "javascript: var xhr = new XMLHttpRequest();" +
                    "xhr.open('GET', '"+ blobUrl +"', true);" +
                    "xhr.setRequestHeader('Content-type','application/pdf');" +
                    "xhr.responseType = 'blob';" +
                    "xhr.onload = function(e) {" +
                    "    if (this.status == 200) {" +
                    "        var blobPdf = this.response;" +
                    "        var reader = new FileReader();" +
                    "        reader.readAsDataURL(blobPdf);" +
                    "        reader.onloadend = function() {" +
                    "            base64data = reader.result;" +
                    "            Android.getBase64FromBlobData(base64data);" +
                    "        }" +
                    "    }" +
                    "};" +
                    "xhr.send();";
        }
        return "javascript: console.log('It is not a Blob URL');";
    }


    public void convertBase64StringToPdfAndStoreIt(String base64PDf) throws IOException {
        Log.e("BASE 64", base64PDf);


        final int notificationId = 1;
        String currentDateTime = DateFormat.getDateTimeInstance().format(new Date());

       String externalFileLoc =  context.getExternalFilesDir
                (Environment.DIRECTORY_DOWNLOADS)
                + "/cpg/";

       File dwldsPath = new File(externalFileLoc);
       if(!dwldsPath.exists())
           dwldsPath.mkdir();


        final File newFileName = new File(externalFileLoc + "/ivycpgHanifa_" + currentDateTime + ".xls");
        newFileName.createNewFile();
        byte[] pdfAsBytes = Base64.decode(base64PDf,Base64.DEFAULT);
//        byte[] pdfAsBytes = Base64.decode("",Base64.DEFAULT);


        FileOutputStream os;
        os = new FileOutputStream(newFileName, false);
        os.write(pdfAsBytes);
        os.flush();

        if (newFileName.exists()) {
            Intent intent = new Intent();
            intent.setAction(android.content.Intent.ACTION_VIEW);
            Uri apkURI = FileProvider.getUriForFile(context,context.getApplicationContext().getPackageName() + ".provider", newFileName);
            intent.setDataAndType(apkURI, MimeTypeMap.getSingleton().getMimeTypeFromExtension("xls"));
            intent.addFlags(Intent.FLAG_GRANT_READ_URI_PERMISSION);
            PendingIntent pendingIntent = PendingIntent.getActivity(context,1, intent, PendingIntent.FLAG_CANCEL_CURRENT);
            String CHANNEL_ID = "MYCHANNEL";
            final NotificationManager notificationManager = (NotificationManager) context.getSystemService(Context.NOTIFICATION_SERVICE);

            if (android.os.Build.VERSION.SDK_INT >= android.os.Build.VERSION_CODES.O) {
                NotificationChannel notificationChannel= new NotificationChannel(CHANNEL_ID,"name", NotificationManager.IMPORTANCE_LOW);
                Notification notification = new Notification.Builder(context,CHANNEL_ID)
                        .setContentText("You have got something new!")
                        .setContentTitle("File downloaded")
                        .setContentIntent(pendingIntent)
                        .setChannelId(CHANNEL_ID)
                        .setSmallIcon(android.R.drawable.sym_action_chat)
                        .build();
                if (notificationManager != null) {
                    notificationManager.createNotificationChannel(notificationChannel);
                    notificationManager.notify(notificationId, notification);
                }

            } else {
                NotificationCompat.Builder b = new NotificationCompat.Builder(context, CHANNEL_ID)
                        .setDefaults(NotificationCompat.DEFAULT_ALL)
                        .setWhen(System.currentTimeMillis())
                        .setSmallIcon(android.R.drawable.sym_action_chat)
                        //.setContentIntent(pendingIntent)
                        .setContentTitle("MY TITLE")
                        .setContentText("MY TEXT CONTENT");

                if (notificationManager != null) {
                    notificationManager.notify(notificationId, b.build());
                    Handler h = new Handler();
                    long delayInMilliseconds = 1000;
                    h.postDelayed(new Runnable() {
                        public void run() {
                            notificationManager.cancel(notificationId);
                        }
                    }, delayInMilliseconds);
                }
            }
        }
        Toast.makeText(context, "PDF FILE DOWNLOADED!", Toast.LENGTH_SHORT).show();
        if (newFileName.exists()) {
            final Uri path;
            final Intent intent = new Intent(Intent.ACTION_VIEW);
            if (Build.VERSION.SDK_INT >= 24) {
                path = FileProvider.getUriForFile(context, context.getApplicationContext().getPackageName() + ".provider", newFileName);
                intent.setDataAndType(path, TYPE_PDF);
                intent.addCategory(Intent.CATEGORY_BROWSABLE);
                intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);
                intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
                intent.setFlags(Intent.FLAG_GRANT_READ_URI_PERMISSION);
            } else {
                path = Uri.fromFile(newFileName);
                intent.setDataAndType(path, TYPE_PDF);
                intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);
            }
            activity.startActivity(intent);
        }

    }
}